import axios from "axios";

const setAuthToken = token => {
  if (token) {
    console.log(token)
    // Apply to every request
    axios.defaults.headers.common["accessToken"] = token;
  } else {
    // Delete auth header
    delete axios.defaults.headers.common["accessToken"];
  }
};

export default setAuthToken;
