import React from "react";
import PropTypes from "prop-types";
import { withRouter } from "react-router-dom";

//redux manenos
import { connect } from "react-redux";
import compose from "recompose/compose";
import { getTasks } from "../actions/taskActions";
import { logoutUser } from "../actions/authActions";
import { clearCurrentProfile } from "../actions/profileActions";

import withStyles from "@material-ui/core/styles/withStyles";

import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Button from "@material-ui/core/Button";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import GridContainer from "../components/Grid/GridContainer.js";
import GridItem from "../components/Grid/GridItem.js";
import TextField from "@material-ui/core/TextField";

const styles = theme => ({
	root: {
		flexGrow: 1
	},

	menuButton: {
		marginRight: theme.spacing(2)
	},
	title: {
		flexGrow: 1
	}
});

const cardStyles = {
	root: {
		minWidth: 275
	},
	bullet: {
		display: "inline-block",
		margin: "0 2px",
		transform: "scale(0.8)"
	},
	title: {
		fontSize: 14
	},
	pos: {
		marginBottom: 12
	}
};

class HomePage extends React.Component {
	constructor(props) {
		super(props);
		this.state = {};
	}

	componentDidMount() {
		if (!this.props.auth.isAuthenticated) {
			this.props.history.push("/");
		}

		this.props.getTasks().then(res => {
			console.log(res);
		});
	}

	onLogoutClick(e) {
		e.preventDefault();
		this.props.clearCurrentProfile();
		this.props.logoutUser();
	}

	render() {
		const { classes } = this.props;
		const tasks = this.props.tasks.tasks.tasks;

		console.log(tasks);
		let tasksData;
		if (tasks && tasks.length > 0) {
			tasksData = tasks.map((task, index) => (
				<GridItem xs={12} sm={12} md={4} key={index}>
					<Card className={classes.cardStyles} variant="outlined">
						<CardContent>
							<Typography variant="body2" component="p">
								Customer First Name: {task.customer_first_name}
							</Typography>
							<Typography variant="body2" component="p">
								Personnel First Name: {task.personnel_first_name}
							</Typography>
						</CardContent>
					</Card>
				</GridItem>
			));
		}
		return (
			<div className={classes.root}>
				<AppBar position="static">
					<Toolbar>
						<IconButton
							edge="start"
							className={classes.menuButton}
							color="inherit"
							aria-label="menu"
						>
							<MenuIcon />
						</IconButton>
						<Typography variant="h6" className={classes.title}>
							task-scheduling-frontend-web
						</Typography>
						<Button color="inherit" onClick={this.onLogoutClick.bind(this)}>
							Logout
						</Button>
					</Toolbar>
				</AppBar>

				<br></br>
				<br></br>

				<form>
					<GridContainer>
						<GridItem xs={12} sm={12} md={3}>
							<TextField required id="page" defaultValue="1" label="Page" />
						</GridItem>
						<GridItem xs={12} sm={12} md={3}>
							<TextField id="standard-basic" label="Limit" />
						</GridItem>
						<GridItem xs={12} sm={12} md={3}>
							<TextField id="standard-basic" label="Order" />
						</GridItem>
						<GridItem xs={12} sm={12} md={3}>
							<TextField id="standard-basic" label="Order By" />
						</GridItem>

						<br></br>
						<br></br>

						<GridContainer>
							<GridItem xs={12} sm={12} md={3}>
								<Button color="primary">Get Tasks</Button>
							</GridItem>
						</GridContainer>
					</GridContainer>
				</form>

				<GridContainer justify="center" spacing={3} style={{ padding: "50px" }}>
					{tasksData}
				</GridContainer>
			</div>
		);
	}
}

HomePage.propTypes = {
	classes: PropTypes.object.isRequired,
	auth: PropTypes.object.isRequired,
	errors: PropTypes.object,
	getTasks: PropTypes.func.isRequired,
	tasks: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
	auth: state.auth,
	errors: state.errors,
	tasks: state.tasks
});

const enhance = compose(
	withStyles(styles),
	connect(mapStateToProps, { getTasks, logoutUser, clearCurrentProfile })
);

export default enhance(withRouter(HomePage));
